// eslint-disable-next-line @typescript-eslint/no-unused-vars
import styles from './app.module.css';
import './app.css';

import React from 'react';

import {GymRecord} from '../entities/GymRecord'

import VerticalContainer from '../vertical-container/VerticalContainer';

import ReadContentBox from '../content-box/ReadContentBox';
import CreateContentBox from '../content-box/CreateContentBox';

export function App() {
    const [records, setRecords] = React.useState<GymRecord[]>([]);

    React.useEffect(() => {
        fetch("http://localhost:8080/gym/records", {
            method: "GET"
        }).then(response => {
            if (response.status == 200) {
                return response.json();
            }
            return null;
        }).then(data => {
            if (data !== null) {
            setRecords(data);
            }
        })
    }, []);

    const handleCreateSubmit = (exercise: string, weight: number) => {
        fetch("http://localhost:8080/gym/records", {
            method: "POST",
            headers: {"content-type": "application/json"},
            body: JSON.stringify({exercise: exercise, weight: weight})
            // this method converts the object to json with the keys and values
            }).then(response => {
                if (response.status == 201) {
                    return response.json()
                }
                return null
            }).then(data => {
                if (data !== null) {
                    setRecords([...records, data]);
                }
            })
    };

    const handleDeleteSubmit = (id: number) => {
        fetch("http://localhost:8080/gym/records/${id}", {
            method: "DELETE"
        }).then(response => {
            if (response.status == 200) {
                return response.json()
            }
            return null;
        }).then(data => {
            if (data !== null) {
                setRecords(records.filter(record => record.id !== data.id));
            }
        })
    };

  return (
    <div className="main-component">
        <div>
            <VerticalContainer>
                {
                    <div>
                        <h2>Create</h2>
                        <CreateContentBox onSubmit={handleCreateSubmit} />
                    </div>
                }
            </VerticalContainer>
        </div>

        <div>
            <VerticalContainer>
                {
                    <div>
                        <h2>Read</h2>
                        {
                            records.map(record => <ReadContentBox
                                key={'${record.id}'}
                                content={record}/>)
                        }
                    </div>
                }
            </VerticalContainer>
        </div>

        <div>
            <VerticalContainer>
                {
                    <div>
                        <h2>Update</h2>
                    </div>
                }
            </VerticalContainer>
        </div>

        <div>
            <VerticalContainer>
                {

                    <div>
                        <h2>Delete</h2>
                        {
                            records.map(record => <DeleteContentBox key={'${record.id}'}
                                onSubmit={handleDeleteSubmit} content={record}
                                />)
                        }
                    </div>
                }
            </VerticalContainer>
        </div>
    </div>
  );
}

export default App;
